sys_script_include.script:
var GenerationUtil = Class.create();
gs.include('Mustache');
GenerationUtil.prototype = {
    initialize: function(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
		this.current=current;
		this.template=template;
		this.email=email;
		this.email_action=email_action;
		this.event=event;
    },

	generate: function (){
		var text=this.getTemplateText();
		var parsed = this.parseMustacheTemplate(text);
		var scripts=parsed.match(Constants.MAILSCRIPT_REGEX);
		if (scripts !== null) {
			var segments = parsed.split(Constants.MAILSCRIPT_REGEX);
			this.template.print(segments[0]);
			for(var i in scripts){
				this.executeMailScript(scripts[i]);
				this.template.print(segments[Number(i)+1]);
			}
		}
		else{
			this.template.print(parsed);
		}
	},

	executeMailScript: function(mailScript) {
		var start = mailScript.indexOf(':');
		var end = mailScript.indexOf('}');
		var scriptName = mailScript.substring(start + 1, end);

		var scriptGR = new GlideRecord('sys_script_email');
		scriptGR.addQuery('name', scriptName);
		scriptGR.setLimit(1);
		scriptGR.query();
		if (scriptGR.next()) {
			try {
				var evaluator = new GlideScopedEvaluator();
				var variables = {
					current: this.current,
					template: this.template,
					email: this.email,
					email_action: this.email_action,
					event: this.event
				};
				evaluator.evaluateScript(scriptGR, 'script', variables);
			} catch (ex) {
				gs.error('Error trying to evaluate email script: ' + ex);
			}
		}

	},

	getTemplateText: function(){
		var text='';
		var templateGR=new GlideRecord(Constants.TEMPLATE_TABLE);
		templateGR.addQuery(Constants.NOTIFICATION_FIELD+'LIKE'+this.email_action.getUniqueValue());
		templateGR.query();
		while(templateGR.next()){
			text += templateGR.getValue(Constants.TEMPLATE_FIELD);
		}
		text = parseEmbeddedTemplates(text);
		text = text.replace(Constants.TRACKER_REGEX,getTrackerTag(this.email_action.getUniqueValue()));

		return text;
	},

	parseMustacheTemplate: function(templateText){
		var props={
			current:this.current
		};
		return Mustache.to_html(templateText, props);
	},

    type: 'GenerationUtil'
};

function parseEmbeddedTemplates(text) {
		var parsedText=text;
		var includes=text.match(Constants.INCLUDE_REGEX);
		if (includes!== null) {
			for(var i in includes){
				var tag=includes[i];
				var name = tag.split('"')[1];
				var template = getTemplateByInternalName(name);
				parsedText=parsedText.replace(tag,template);
			}
		}
		return parsedText;
};
function getTrackerTag(notificationSysId){
		var instanceUrl = gs.getProperty('glide.servlet.uri');
		var scope = gs.getCurrentScopeName();
		var url = instanceUrl+'api/'+scope+'/engagement/opened?notification='+notificationSysId; 
		return '<div style="background-image:url(\''+url+'\')" ></div>';
};
function getTemplateByInternalName(internalName){
		var templateGR=new GlideRecord(Constants.TEMPLATE_TABLE);
		templateGR.addQuery(Constants.INTERNALNAME_FIELD,internalName);
		templateGR.setLimit(1);
		templateGR.query();
		if(templateGR.next()){
			return templateGR.getValue(Constants.TEMPLATE_FIELD);
		}
		return '<span style="color:red">Could not find a template with Internal Name "'+internalName+'"</span>';
};
