sys_script_include.script:
var EngagementHelper = Class.create();
EngagementHelper.prototype = {
    initialize: function() {
    },

	getPixelStream: function() {
		var attachmentStream= new GlideSysAttachment().getContentStream(Constants.PIXEL_SYSID);
		return attachmentStream;
	},

	addEngagement: function(notification,headers){
		if(notification){
			this.createMetric({
				table:'sysevent_email_action',
				id: notification,
				metric_type: 'open'
			});

		}
	},

	createMetric: function(values){
		var metricGR = new GlideRecord(Constants.METRIC_TABLE);
		metricGR.initialize();
		for(var prop in values){
			metricGR.setValue(prop,values[prop]);
		}
		return metricGR.insert();
	},

    type: 'EngagementHelper'
};
